mapper.insert:
INSERT INTO students (
               student_name
            , student_email
            , class_id
)
    VALUE (
    #{student.studentName}
    , #{student.studentEmail}
    , #{student.classId}
    )

mapper.update:
UPDATE students
SET student_name = #{student.studentName}
    , student_email = #{student.studentEmail}
    , class_id = #{student.classId}
WHERE student_id = #{student.studentId}
